import request from '@/utils/request'

export function getOpinionList(params) {
  return request({
    url: '/opinion/list',
    method: 'get',
    params
  })
}

export function getOpinionAnalysis(params) {
  return request({
    url: '/opinion/analysis',
    method: 'get',
    params
  })
}

export function exportOpinionData(params) {
  return request({
    url: '/opinion/export',
    method: 'get',
    params,
    responseType: 'blob'
  })
}
